$(document).ready(function(){$("#logout").bind("click",function(){window.location="index.php"});$("#add_btn").bind("click",function(){var b=$('#name_txt').val();var c=$('#message_ta').val();var d=$('#mess_inputs').val();$.ajax({type:"GET",url:"functions.php",data:{function:"add",name:b,message:c,pid:d}}).done(function(a){alert(a);});});});function changeProducer(c){$("#mess_inputs").val(c);$("#mess_p_mess").empty();$.ajax({type:"GET",url:"functions.php",data:{function:"producers",pid:c}}).done(function(a){var j=JSON.parse(a);$("#mess_p_headline").text("Meddelande till "+j.name+", "+j.city);if(j.url!==""){$("#mess_p_kontakt").text("LÃ¤nk till deras hemsida "+j.url)}else{$("#mess_p_kontakt").text("Producenten har ingen webbsida")}if(j.imageURL!==""){$("#p_img_link").attr("href",j.imageURL);$("#p_img").attr("src",j.imageURL)}else{$("#p_img_link").attr("href","#");$("#p_img").attr("src","img/noimg.jpg")}});$.ajax({type:"GET",url:"functions.php",data:{function:"getMessages",pid:c}}).done(function(b){var j=JSON.parse(b);if(j.length>0){j.reverse().forEach(function(a){$("#mess_p_mess").append("<p class='message_container'>"+a.message+"<br />Skrivet av: "+a.name+"</p>")})}});$("#mess_container").show("slow")}